*{
    box-sizing:border-box;
}
body {
    margin:0;
    font-family: sans-serif;
    color: #999;
}
a, a:visited {
    text-decoration:none;
}
.movie-list .movie{
    width:250px;
    float:left;
    margin-right:25px;
}
.movie-list .movie img{
    width:100%;
}
.movie-list .movie a.title{
    text-decoration:none;
    color:#999;
    font-weight:bold;
    font-size:18px;
    line-height:25px;
}
.movie-list .movie .synopsis{
    font-size:14px;
    line-height:20px;
}
